import 'dart:math';
import 'dart:ui';

import 'package:flutter/material.dart';

class PaintDemo extends StatefulWidget {
  const PaintDemo({Key? key}) : super(key: key);

  @override
  State<StatefulWidget> createState() => _PaintDemoState();
}

class _PaintDemoState extends State<PaintDemo> {
  //最后一次down事件的offset
  Offset lastStartOffset = Offset(0, 0);
  int behavior = -1;

  final List<Offset> _points = <Offset>[];

  @override
  void initState() {
    super.initState();
    //rectStartX=100;rectStartY=100;mapStartX=200;mapStartY=200;
    rectStartOffset = Offset(100, 100);
    mapStartOffset = Offset(100, 200);
    lastStartOffset = Offset(0, 0);
    mapLastStartOffset = Offset(0, 0);
    rectLastStartOffset = Offset(0, 0);
    print("--initState--");
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: const Text("绘制图形"),
      ),
      body: Container(
        color: Colors.white60,
        child: LayoutBuilder(
          builder: (context, constraints) {
            return InteractiveViewer(
              boundaryMargin: EdgeInsets.symmetric(
                horizontal: constraints.maxWidth,
                vertical: constraints.maxHeight,
              ),
              maxScale: 10,
              child: SizedBox.expand(
                child: GestureDetector(
                  onPanDown: (details) {
                    setState(() {
                      lastStartOffset = details.localPosition;
                      behavior = 0;
                    });
                  },
                  onPanUpdate: (details) {
                    setState(() {
                      lastStartOffset = details.localPosition;
                      behavior = 1;
                    });
                  },
                  onPanEnd: (details) {
                    setState(() {
                      behavior = 2;
                    });
                  },
                  child: CustomPaint(
                    painter: _MyPainter(
                        lastStartOffset: lastStartOffset, behavior: behavior),
                  ),
                ),
              ),
            );
          },
        ),
      ),
      persistentFooterButtons: [_resetButton, _editButton],
    );
  }

  IconButton get _resetButton {
    return IconButton(
      onPressed: () {
        setState(() {
          //rectStartX=100;rectStartY=100;mapStartX=200;mapStartY=200;
          rectStartOffset = Offset(100, 100);
          mapStartOffset = Offset(100, 200);
          lastStartOffset = Offset(0, 0);
          mapLastStartOffset = Offset(0, 0);
          rectLastStartOffset = Offset(0, 0);
        });
      },
      tooltip: 'Reset',
      color: Colors.indigo,
      icon: const Icon(Icons.replay),
    );
  }

  IconButton get _editButton {
    return IconButton(
      onPressed: () {
        // setState(() {
        //   //_animateResetInitialize();
        // });
      },
      tooltip: 'edit',
      color: Colors.blue,
      icon: const Icon(Icons.edit),
    );
  }
}

