  @override
  void paint(Canvas canvas, Size size) {
    Paint paint = Paint()
      ..strokeWidth = 1
      ..isAntiAlias = false;
    if (lastStartOffset.dx < rectStartOffset.dx ||
        lastStartOffset.dx > rectStartOffset.dx + rectWidth ||
        lastStartOffset.dy < rectStartOffset.dy ||
        lastStartOffset.dy > rectStartOffset.dy + rectHeight) {
      if (behavior == 1) {
        mapStartOffset += lastStartOffset - mapLastStartOffset;
      }
      mapLastStartOffset = lastStartOffset;
    } else {
      if (behavior == 1) {
        rectStartOffset += lastStartOffset - rectLastStartOffset;
      }
      rectLastStartOffset = lastStartOffset;
    }

    List<Offset> greyPoints = [];
    List<Offset> whitePoints = [];
    List<Offset> blackPoints = [];
    var data = data00;
    var length = data.length;

    for (var i = 0; i < length; i++) {
      if (data[i] == -1) {
        greyPoints.add(Offset(i % 74, i / 74) + mapStartOffset);
      } else if (data[i] == 0) {
        whitePoints.add(Offset(i % 74, i / 74) + mapStartOffset);
      } else if (data[i] == 100) {
        blackPoints.add(Offset(i % 74, i / 74) + mapStartOffset);
      }
    }
    paint.color = Colors.grey;
    canvas.drawPoints(PointMode.points, greyPoints, paint);
    paint.color = Colors.white;
    canvas.drawPoints(PointMode.points, whitePoints, paint);
    paint.color = Colors.black;
    canvas.drawPoints(PointMode.points, blackPoints, paint);

    // 画裁剪框
    paint.color = Colors.blue;
    List<Offset> points2 = [
      rectStartOffset,
      rectStartOffset + Offset(rectWidth, 0),
      rectStartOffset + Offset(rectWidth, rectHeight),
      rectStartOffset + Offset(0, rectHeight),
      rectStartOffset,
    ];
    canvas.drawPoints(PointMode.polygon, points2, paint); //draw the clip box
    paint.color = Colors.red;
    double radius = 3;
    canvas.drawCircle(points2[0], radius, paint); //draw the drag point
    canvas.drawCircle(points2[1], radius, paint);
    canvas.drawCircle(points2[2], radius, paint);
    canvas.drawCircle(points2[3], radius, paint);
  }

  @override
  bool shouldRepaint(covariant _MyPainter oldDelegate) {
    return oldDelegate.lastStartOffset != lastStartOffset ||
        oldDelegate.behavior != behavior;
  }
