  @override
  void initState() {
    super.initState();
    rectWidth = 100;
    rectHeight = 100;
    rectStartOffset = const Offset(100, 100);
    mapStartOffset = const Offset(100, 200);
    mapLastStartOffset = const Offset(0, 0);
    rectLastStartOffset = const Offset(0, 0);
    rectWidthLastOffset = const Offset(0, 0);
    print("--initState--");
  }

  _onPanDown(DragDownDetails details) {
    setState(() {
      var dx = details.localPosition.dx - rectStartOffset.dx - rectWidth;
      var dy = details.localPosition.dy - rectStartOffset.dy - rectHeight;
      if ((dx * dx + dy * dy) < 400) {
        behavior = 2;
        rectWidthLastOffset = details.localPosition;
      } else if (details.localPosition.dx < rectStartOffset.dx ||
          details.localPosition.dx > rectStartOffset.dx + rectWidth ||
          details.localPosition.dy < rectStartOffset.dy ||
          details.localPosition.dy > rectStartOffset.dy + rectHeight) {
        behavior = 0;
        mapLastStartOffset = details.localPosition;
      } else {
        behavior = 1;
        rectLastStartOffset = details.localPosition;
      }
    });
  }

  _onPanUpdate(DragUpdateDetails details) {
    setState(() {
      if (behavior == 0) {
        mapStartOffset += details.localPosition - mapLastStartOffset;
        mapLastStartOffset = details.localPosition;
      } else if (behavior == 1) {
        rectStartOffset += details.localPosition - rectLastStartOffset;
        rectLastStartOffset = details.localPosition;
      } else if (behavior == 2) {
        var offset = details.localPosition - rectWidthLastOffset;
        rectWidth += offset.dx;
        rectHeight += offset.dy;
        rectWidthLastOffset = details.localPosition;
      }
    });
  }

  _onPanEnd(DragEndDetails details) {
    setState(() {
      behavior = -1;
    });
  }

  IconButton get _resetButton {
    return IconButton(
      onPressed: () {
        setState(() {
          rectWidth = 100;
          rectHeight = 100;
          rectStartOffset = const Offset(100, 100);
          mapStartOffset = const Offset(100, 200);
        });
      },
      tooltip: 'Reset',
      color: Colors.indigo,
      icon: const Icon(Icons.replay),
    );
  }

  IconButton get _editButton {
    return IconButton(
      onPressed: () {
        // setState(() {
        //   //_animateResetInitialize();
        // });
      },
      tooltip: 'edit',
      color: Colors.blue,
      icon: const Icon(Icons.edit),
    );
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: const Text("绘制图形"),
      ),
      body: Container(
        color: Colors.white60,
        child: LayoutBuilder(
          builder: (context, constraints) {
            return InteractiveViewer(
              boundaryMargin: EdgeInsets.symmetric(
                horizontal: constraints.maxWidth,
                vertical: constraints.maxHeight,
              ),
              maxScale: 10,
              child: SizedBox.expand(
                child: GestureDetector(
                  onPanDown: _onPanDown,
                  onPanUpdate: _onPanUpdate,
                  onPanEnd: _onPanEnd,
                  child: CustomPaint(
                    painter: _MyPainter(
                        data: data00,
                        mapStartOffset: mapStartOffset,
                        rectStartOffset: rectStartOffset,
                        rectWidth: rectWidth,
                        rectHeight: rectHeight),
                  ),
                ),
              ),
            );
          },
        ),
      ),
      persistentFooterButtons: [_resetButton, _editButton],
    );
  }
}

class _MyPainter extends CustomPainter {
  _MyPainter(
      {required this.data,
        required this.mapStartOffset,
        required this.rectStartOffset,
        required this.rectWidth,
        required this.rectHeight});

  Offset rectStartOffset;
  Offset mapStartOffset;
  double rectWidth, rectHeight;
  var data;

  @override
  void paint(Canvas canvas, Size size) {
    Paint paint = Paint()
      ..strokeWidth = 1
      ..isAntiAlias = false;

    List<Offset> greyPoints = [];
    List<Offset> whitePoints = [];
    List<Offset> blackPoints = [];
    var length = data.length;

    for (var i = 0; i < length; i++) {
      if (data[i] == -1) {
        greyPoints.add(Offset(i % 74, i / 74) + mapStartOffset);
      } else if (data[i] == 0) {
        whitePoints.add(Offset(i % 74, i / 74) + mapStartOffset);
      } else if (data[i] == 100) {
        blackPoints.add(Offset(i % 74, i / 74) + mapStartOffset);
      }
    }
    paint.color = Colors.grey;
    canvas.drawPoints(PointMode.points, greyPoints, paint);
    paint.color = Colors.white;
    canvas.drawPoints(PointMode.points, whitePoints, paint);
    paint.color = Colors.black;
    canvas.drawPoints(PointMode.points, blackPoints, paint);

    // 画裁剪框
    paint.color = Colors.blue;
    List<Offset> points2 = [
      rectStartOffset,
      rectStartOffset + Offset(rectWidth, 0),
      rectStartOffset + Offset(rectWidth, rectHeight),
      rectStartOffset + Offset(0, rectHeight),
      rectStartOffset,
    ];
    canvas.drawPoints(PointMode.polygon, points2, paint); //draw the clip box
    paint.color = Colors.red;
    double radius = 3;
    canvas.drawCircle(points2[0], radius, paint); //draw the drag point
    canvas.drawCircle(points2[1], radius, paint);
    canvas.drawCircle(points2[2], radius * 3, paint);
    canvas.drawCircle(points2[3], radius, paint);
  }

  @override
  bool shouldRepaint(covariant _MyPainter oldDelegate) {
    return oldDelegate.mapStartOffset != mapStartOffset ||
        oldDelegate.rectStartOffset != rectStartOffset ||
        oldDelegate.rectWidth != rectWidth ||
        oldDelegate.rectHeight != rectHeight;
  }
}
